import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ManagerDashboard.css';
import api from '../api'; // Ensure this points to your API setup

const ManagerDashboard = () => {
  const [agents, setAgents] = useState([]);
  const [ticketCounts, setTicketCounts] = useState([]);
  const [tickets, setTickets] = useState([]); // Define the tickets state
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newAgentId, setNewAgentId] = useState(''); // New agent for reassignment

  const navigate = useNavigate();

  useEffect(() => {
    setLoading(true);
    fetchAgentsAndTickets(); // Fetch both agents and tickets
  }, []);

  // Fetch agents and tickets data from the API
  const fetchAgentsAndTickets = async () => {
    try {
      const [agentsResponse, ticketCountsResponse, ticketsResponse] = await Promise.all([
        api.get('/users/allAgentsDetails'), // Fetch agent details
        api.get('/tickets/ticketCounts'),    // Fetch ticket counts for agents
        api.get('/tickets/tickets')          // Fetch individual ticket details
      ]);

      setAgents(agentsResponse.data);
      setTicketCounts(ticketCountsResponse.data);
      setTickets(ticketsResponse.data); // Store individual ticket details
    } catch (error) {
      setError('Error fetching data.');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Function to get ticket count for an agent
  const getTicketCountForAgent = (agentId) => {
    const agentTicket = ticketCounts.find(ticket => ticket.agentId === agentId);
    return agentTicket ? agentTicket.ticketCount : 0; // Return 0 if no tickets
  };

  // Handle ticket reassignment
  const handleReassignTicket = async (ticketId) => {
    try {
      await api.put(`/tickets/tickets/${ticketId}/reassign`, { newAgentId }); // Use template literals for URL
      fetchAgentsAndTickets(); // Refresh data after reassigning
    } catch (error) {
      console.error('Error reassigning ticket:', error);
    }
  };

  // Loading and Error Handling
  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="manager-dashboard-container">
      <h2>Manager Dashboard</h2>

      {/* Display list of agents and their ticket counts */}
      <div className="agents-container">
        <h3>Agents</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Agent Name</th>
              <th>Assigned Tickets</th>
            </tr>
          </thead>
          <tbody>
            {agents.map(agent => (
              <tr key={agent._id}>
                <td>{agent.name}</td>
                <td>{getTicketCountForAgent(agent._id)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Display all tickets with reassignment options */}
      <div className="tickets-container">
        <h3>Tickets</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Title</th>
              <th>Assigned Agent</th>
              <th>Reassign</th>
            </tr>
          </thead>
          <tbody>
            {tickets.map(ticket => (
              <tr key={ticket._id}>
                <td>{ticket._id}</td>
                <td>{ticket.title}</td>
                <td>{ticket.assignedAgent ? agents.find(agent => agent._id === ticket.assignedAgent)?._id : 'Unassigned'}</td>
                <td>
                  <select
                    value={newAgentId}
                    onChange={(e) => setNewAgentId(e.target.value)}
                  >
                    <option value="">Select Agent</option>
                    {agents.map(agent => (
                      <option key={agent._id} value={agent._id}>
                        {agent.name}
                      </option>
                    ))}
                  </select>
                  <button onClick={() => handleReassignTicket(ticket._id)}>Reassign</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManagerDashboard;
