import React, { useEffect, useState } from 'react';
import './ManagerDashboard.css';
import api from '../api'; // Ensure this points to your API setup
import TicketModal from './TicketModal'; // Import the TicketModal component

const ManagerDashboard = () => {
  const [agents, setAgents] = useState([]);
  const [ticketCounts, setTicketCounts] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [ticketAssignments, setTicketAssignments] = useState({});
  const [selectedTicket, setSelectedTicket] = useState(null); // State for selected ticket
  const [showModal, setShowModal] = useState(false); // State to control the modal

  useEffect(() => {
    setLoading(true);
    fetchAgentsAndTickets(); // Fetch both agents and tickets
  }, []);

  const fetchAgentsAndTickets = async () => {
    try {
      const [agentsResponse, ticketCountsResponse, ticketsResponse] = await Promise.all([
        api.get('/users/allAgentsDetails'),
        api.get('/tickets/ticketCounts'),
        api.get('/tickets/tickets')
      ]);

      setAgents(agentsResponse.data);
      setTicketCounts(ticketCountsResponse.data);
      setTickets(ticketsResponse.data);

      const initialAssignments = ticketsResponse.data.reduce((acc, ticket) => {
        acc[ticket._id] = ticket.assignedTo ? ticket.assignedTo._id : '';
        return acc;
      }, {});
      setTicketAssignments(initialAssignments);
    } catch (error) {
      setError('Error fetching data.');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Function to get ticket count for an agent
  const getTicketCountForAgent = (agentId) => {
    const agentTicket = ticketCounts.find(ticket => ticket.agentId === agentId);
    return agentTicket ? agentTicket.ticketCount : 0; // Return 0 if no tickets
  };

  const handleReassignTicket = async (ticketId) => {
    try {
      const newAgentId = ticketAssignments[ticketId];
      await api.put(`/tickets/tickets/${ticketId}/reassign`, { newAgentId });
      fetchAgentsAndTickets(); // Refresh data after reassigning
    } catch (error) {
      console.error('Error reassigning ticket:', error);
    }
  };

  const handleViewTicket = (ticket) => {
    setSelectedTicket(ticket); 
    setShowModal(true); 
  };

  const handleCloseModal = () => {
    fetchAgentsAndTickets();
    setShowModal(false); 
    setSelectedTicket(null); 
  };

  const handleTicketAction = async (updatedTicket) => {
    try {
      await api.put(`/tickets/tickets/${updatedTicket._id}`, updatedTicket); // Update ticket
      fetchAgentsAndTickets(); // Refresh data after updating
    } catch (error) {
      console.error('Error updating ticket:', error);
    }
  };
  
  const handleLogout = () => {
    sessionStorage.clear();
    navigate('/');
};

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="manager-dashboard-container">
      <h2>Manager Dashboard</h2>
      <div className="btn-toolbar">
          <button className="logout-button" onClick={handleLogout}>Logout</button>
          </div>

      {/* Display list of agents and their ticket counts */}
      <div className="agents-container">
        <h3>Agents</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Agent Name</th>
              <th>Assigned Tickets</th>
            </tr>
          </thead>
          <tbody>
            {agents.map(agent => (
              <tr key={agent._id}>
                <td>{agent.name}</td>
                <td>{getTicketCountForAgent(agent._id)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Display all tickets with reassignment options */}
      <div className="tickets-container">
        <h3>Tickets</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Title</th>
              <th>Status</th>
              <th>Assigned Agent</th>
              <th>Reassign</th>
              <th>View</th> {/* New column */}
            </tr>
          </thead>
          <tbody>
            {tickets.map(ticket => (
              <tr key={ticket._id}>
                <td>{ticket._id}</td>
                <td>{ticket.title}</td>
                <td>{ticket.status}</td>
                <td>{ticket.assignedTo ? ticket.assignedTo.name : 'Unassigned'}</td>
                <td>
                  <select
                    value={ticketAssignments[ticket._id] || ''}
                    onChange={(e) => setTicketAssignments(prevAssignments => ({
                      ...prevAssignments,
                      [ticket._id]: e.target.value
                    }))}
                  >
                    <option value="">Select Agent</option>
                    {agents.map(agent => (
                      <option key={agent._id} value={agent._id}>
                        {agent.name}
                      </option>
                    ))}
                  </select>
                  <button onClick={() => handleReassignTicket(ticket._id)}>Reassign</button>
                </td>
                <td>
                  <button onClick={() => handleViewTicket(ticket)}>View</button> {/* View button */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && (
        <TicketModal
          ticket={selectedTicket}
          onClose={handleCloseModal}
          onAction={handleTicketAction}
        />
      )}
    </div>
  );
};

export default ManagerDashboard;
