import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ManagerDashboard.css';
import api from '../api'; // Ensure this points to your API setup

const ManagerDashboard = () => {
  const [agents, setAgents] = useState([]);
  const [ticketCounts, setTicketCounts] = useState([]);
  const [tickets, setTickets] = useState([]); // Define the tickets state
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [ticketAgentAssignments, setTicketAgentAssignments] = useState({}); // Ticket-specific reassignment state
  const [showModal, setShowModal] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    setLoading(true);
    fetchAgentsAndTickets(); // Fetch both agents and tickets
    fetchTickets();
  }, []);

  // Fetch agents and tickets data from the API
  const fetchAgentsAndTickets = async () => {
    try {
      const [agentsResponse, ticketCountsResponse, ticketsResponse] = await Promise.all([
        api.get('/users/allAgentsDetails'), // Fetch agent details
        api.get('/tickets/ticketCounts'),    // Fetch ticket counts for agents
        api.get('/tickets/tickets')          // Fetch individual ticket details
      ]);

      setAgents(agentsResponse.data);
      setTicketCounts(ticketCountsResponse.data);
      setTickets(ticketsResponse.data); // Store individual ticket details

      // Initialize ticketAgentAssignments state
      const initialAssignments = ticketsResponse.data.reduce((acc, ticket) => {
        acc[ticket._id] = ticket.assignedTo ? ticket.assignedTo._id : ''; // Set initial value
        return acc;
      }, {});
      setTicketAgentAssignments(initialAssignments);

    } catch (error) {
      setError('Error fetching data.');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchTickets = async () => {
    try {
      const response = await api.get(`/tickets/assigned/${user._id}`); // Adjust endpoint as needed
      const sortedTickets = response.data.sort((a, b) => {
        if (a.category === b.category) return 0;
        return (categoryOrder[a.category] || 999) - (categoryOrder[b.category] || 999);
      });
      setTickets(sortedTickets);
    } catch (error) {
      setError('Error fetching tickets.');
      console.error('Error fetching tickets:', error);
    } finally {
      setLoading(false);
    }
  };

  // Function to get ticket count for an agent
  const getTicketCountForAgent = (agentId) => {
    const agentTicket = ticketCounts.find(ticket => ticket.agentId === agentId);
    return agentTicket ? agentTicket.ticketCount : 0; // Return 0 if no tickets
  };

  const handleViewTicket = (ticket) => {
    setSelectedTicket(ticket); 
    setShowModal(true); 
  };

  const handleCloseModal = () => {
    fetchTickets();
    setShowModal(false); 
    setSelectedTicket(null); 
  };

  const handleTicketAction = (action) => {
    console.log(`Ticket action: ${action}`); // Implement your logic for ticket actions here
    handleCloseModal(); // Close the modal after action
  };

  // Handle ticket reassignment
  const handleReassignTicket = async (ticketId) => {
    try {
      const newAgentId = ticketAgentAssignments[ticketId];
      await api.put(`/tickets/tickets/${ticketId}/reassign`, { newAgentId });
      fetchAgentsAndTickets(); // Refresh data after reassigning
      window.location.reload();
    } catch (error) {
      console.error('Error reassigning ticket:', error);
    }
  };

  // Update the state when a new agent is selected for a specific ticket
  const handleAgentChange = (ticketId, agentId) => {
    setTicketAgentAssignments(prevAssignments => ({
      ...prevAssignments,
      [ticketId]: agentId
    }));
  };

  // Loading and Error Handling
  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="manager-dashboard-container">
      <h2>Manager Dashboard</h2>

      {/* Display list of agents and their ticket counts */}
      <div className="agents-container">
        <h3>Agents</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Agent Name</th>
              <th>Assigned Tickets</th>
            </tr>
          </thead>
          <tbody>
            {agents.map(agent => (
              <tr key={agent._id}>
                <td>{agent.name}</td>
                <td>{getTicketCountForAgent(agent._id)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Display all tickets with reassignment options */}
      <div className="tickets-container">
        <h3>Tickets</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Title</th>
              <th>Assigned Agent</th>
              <th>Reassign</th>
              <th>View Ticket</th>
            </tr>
          </thead>
          <tbody>
            {tickets.map(ticket => (
              <tr key={ticket._id}>
                <td>{ticket._id}</td>
                <td>{ticket.title}</td>
                <td>{ticket.assignedTo ? ticket.assignedTo.name : 'Unassigned'}</td>
                <td>
                  <select
                    value={ticketAgentAssignments[ticket._id] || ''}
                    onChange={(e) => handleAgentChange(ticket._id, e.target.value)}
                  >
                    <option value="">Select Agent</option>
                    {agents.map(agent => (
                      <option key={agent._id} value={agent._id}>
                        {agent.name}
                      </option>
                    ))}
                  </select>
                  <button onClick={() => handleReassignTicket(ticket._id)}>Reassign</button>
                </td>
                <td><button className="view-link" onClick={() => handleViewTicket(ticket)}>View</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManagerDashboard;
