import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import './ManagerDashboard.css';
import { UserContext } from "../UserContext";
import api from '../api'; // Make sure to adjust this with your actual API setup

const ManagerDashboard = () => {
  const [agents, setAgents] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTicket, setSelectedTicket] = useState(null); // For reassigning tickets
  const [newAgentId, setNewAgentId] = useState(''); // New agent for reassignment

  const navigate = useNavigate();

  useEffect(() => {
    setLoading(true);
    fetchAgentsAndTickets();
  }, []);

  // Fetch agents and tickets data from the API
  const fetchAgentsAndTickets = async () => {
    try {
      const [agentsResponse, ticketsResponse] = await Promise.all([
        api.get('/users/allAgentsDetails'), 
        api.get('/tickets/tickets') 
      ]);
      setAgents(agentsResponse.data);
      setTickets(ticketsResponse.data);
    } catch (error) {
      setError('Error fetching data.');
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Function to count tickets assigned to each agent
  const getTicketCountForAgent = (agentId) => {
    return tickets.filter(ticket => ticket.assignedAgent?._id === agentId).length;
  };

  // Handle ticket reassignment
  const handleReassignTicket = async (ticketId) => {
    try {
      await api.put(`/tickets/tickets/${ticketId}/reassign`, { newAgentId }); // Adjust API and payload
      fetchAgentsAndTickets(); // Refresh data
    } catch (error) {
      console.error('Error reassigning ticket:', error);
    }
  };

  // Loading and Error Handling
  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="manager-dashboard-container">
      <h2>Manager Dashboard</h2>

      {/* Display list of agents */}
      <div className="agents-container">
        <h3>Agents</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Agent Name</th>
              <th>Assigned Tickets</th>
            </tr>
          </thead>
          <tbody>
            {agents.map(agent => (
              <tr key={agent._id}>
                <td>{agent.name}</td>
                <td>{getTicketCountForAgent(agent._id)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Display all tickets with reassignment options */}
      <div className="tickets-container">
        <h3>Tickets</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Title</th>
              <th>Assigned Agent</th>
              <th>Reassign</th>
            </tr>
          </thead>
          <tbody>
            {tickets.map(ticket => (
              <tr key={ticket._id}>
                <td>{ticket._id}</td>
                <td>{ticket.title}</td>
                <td>{ticket.assignedAgent ? ticket.assignedAgent.name : 'Unassigned'}</td>
                <td>
                  <select
                    value={newAgentId}
                    onChange={(e) => setNewAgentId(e.target.value)}
                  >
                    <option value="">Select Agent</option>
                    {agents.map(agent => (
                      <option key={agent._id} value={agent._id}>
                        {agent.name}
                      </option>
                    ))}
                  </select>
                  <button onClick={() => handleReassignTicket(ticket._id)}>Reassign</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManagerDashboard;
